//import logo from './logo.svg';
import './App.css';
import { useState } from 'react';

const App = () => {
  const [value, setValue] = useState(0); // Inicializamos el estado con 0
  const Increment = () =>{
    setValue(value + 1)
  }

  const Decrement = () =>{
    setValue(value - 1)
  }
  return (
    <div className="App">
      <main>
        Hola mundo
      </main>
      <button onClick={Increment}> {/* Usamos una función arrow para ejecutar setValue */}
        Increment number
      </button>
      <button onClick={Decrement}> {/* Usamos una función arrow para ejecutar setValue */}
        Decrement number
      </button>
      <h1>
      {"Value equals = " + value }
      </h1>
    </div>
  );
}

export default App;
