import logo from './logo.svg';
import './App.css';
import { useState } from 'react';

const App = () => {
  const [value, setValue] = useState(0); // Inicializamos el estado con 0

  return (
    <div className="App">
      <main>
        Hola mundo
      </main>
      <button onClick={() => setValue(value + 1)}> {/* Usamos una funci√≥n arrow para ejecutar setValue */}
        {"Value equals = " + value }
      </button>
    </div>
  );
}

export default App;
